mapper.select:
SELECT inbaId, inbaCode, inbaName, busiId, busiName, busiDeptId, inbtId, inbaReason, inbaDate, inbaRemark, inbaState, approvalview, state, inbaDelflag, optId, inbaCurrdate FROM t_erp_inbuyapply WHERE inbaDelflag=0
and inbaName like '%${searchValue}%'
order by inbaCurrdate desc limit #{pageBean.start},#{pageBean.rows}

SELECT inbaId, inbaCode, inbaName, busiId, busiName, busiDeptId, inbtId, inbaReason, inbaDate, inbaRemark, inbaState, approvalview, state, inbaDelflag, optId, inbaCurrdate FROM t_erp_inbuyapply WHERE inbaDelflag=0 and inbaState>=1
and inbaName like '%${s_inbaName}%'
and busiName like '%${s_busiName}%'
and inbaState = ${s_inbaState}
and inbtId =${s_inbtId}
order by inbaCurrdate desc limit #{pageBean.start},#{pageBean.rows}

SELECT inbaId, inbaCode, inbaName, busiId, busiName, busiDeptId, inbtId, inbaReason, inbaDate, inbaRemark, inbaState, approvalview, state, inbaDelflag, optId, inbaCurrdate FROM t_erp_inbuyapply WHERE inbaDelflag=0
and inbaName like '%${s_inbaName}%'
and busiName like '%${s_busiName}%'
and inbaState = ${s_inbaState}
and inbtId =${s_inbtId}
order by inbaCurrdate desc limit #{pageBean.start},#{pageBean.rows}

select count(1) from t_erp_inbuyapply where inbaDelflag=0
and inbaName like '%${s_inbaName}%'
and busiName like '%${s_busiName}%'
and inbaState = ${s_inbaState}
and inbtId =${s_inbtId}

mapper.update:
UPDATE 
t_erp_inbuyapply
SET
inbaReason=#{inbaReason}		
WHERE
inbaId =#{inbaId}

UPDATE t_erp_inbuyapply
	SET
	inbaState=-1,
	approvalview=#{approvalview}			
	WHERE
	inbaId=#{inbaId}
