mapper.select:
select tei.ingcId, tei.ingcCode, tei.ingcName, tei.ingcSpell, tei.ingcRemark, tei.ingcState, tei.ingcDelflag, tei.optId, tei.currDate, teu.userName optName from t_erp_ingoodscategory tei left join t_erp_user teu on tei.optId=teu.userId where tei.ingcDelflag=0
and tei.ingcName like '%${s_ingcName}%'
order by tei.currDate desc limit #{pageBean.start},#{pageBean.rows}

mapper.update:
update t_erp_ingoodscategory
set    ingcDelflag=1
where  ingcId=#{ingcId}
