mapper.select:
SELECT tei.ingtId, tei.ingtCode, tei.ingtName, tei.ingtSpell, tei.ingtRemark, tei.ingtState, tei.ingtDelflag, tei.optId, tei.currDate, teu.userName optName FROM t_erp_ingoodstype tei LEFT JOIN t_erp_user teu ON tei.optId=teu.`userId` WHERE tei.ingtDelflag=0
and tei.ingtCode like CONCAT('%',#{inGoodsType.ingtCode},'%')
and tei.ingtName = #{inGoodsType.ingtName}
order by tei.currDate desc limit #{pageBean.start},#{pageBean.rows}

SELECT count(1) FROM t_erp_ingoodstype WHERE ingtDelflag=0
and ingtCode like CONCAT('%',#{inGoodsType.ingtCode},'%')
and ingtName = #{inGoodsType.ingtName}
