mapper.select:
select tu.userId, tu.state, tu.userDescription, tu.userDelflag, tu.currentTime, tu.optId, tr.roleName, tr.roleId, tu.userName, tu.password from t_erp_user tu left join t_erp_role tr on tu.roleId = tr.roleId where tu.userDelflag=0 and tr.roleDelflag=0
and tu.userName like CONCAT('%',#{userInfo.userName},'%')
and tu.roleId = #{userInfo.roleId}
order by tu.currentTime desc limit #{pageBean.start},#{pageBean.rows}

select count(*) from t_erp_user where userDelflag=0
and userName like CONCAT('%',#{userInfo.userName},'%')
and roleId = #{userInfo.roleId}
